import React, { useEffect, useState } from 'react';
import Navbar from '../components/ui/Navbar';
import Footer from '../components/ui/Footer';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import toast from 'react-hot-toast';

interface Pedido {
    pedido: string;
    cant: number;
    precio: number;
}

interface Mesa {
    id?: string;
    mesaId: number;
    personas: number;
    status: boolean;
    statusPedido: number;
    pedido: Pedido[];
    createdAt: string;
}

const TablesPage = () => {
    const base64Img =
        'data:image/png;base64,';
    const [mesas, setMesas] = useState<Mesa[]>([]);
    const [editingMesa, setEditingMesa] = useState<Mesa | null>(null);
    const [newMesa, setNewMesa] = useState<Mesa>({
        mesaId: 0,
        personas: 0,
        status: true,
        statusPedido: 1,
        pedido: [],
        createdAt: "",
    });
    const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
    const [showPayModal, setShowPayModal] = useState<boolean>(false);
    const [selectedMesa, setSelectedMesa] = useState<Mesa | null>(null);
    const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
    const [isConfirm, setisConfirm] = useState(false)



    const generatePDF = (mesa: Mesa) => {
        const doc = new jsPDF('p', 'pt', 'letter'); // p=portrait, pt=points, letter=8.5x11in

        // Tamaño de la página en puntos
        const pageWidth = doc.internal.pageSize.getWidth();
        const margin = 40;

        // Agregar la imagen al PDF
        const imageWidth = 90;  // Ancho de la imagen más grande
        const imageHeight = 90; // Alto de la imagen más grande
        const imageX = pageWidth - imageWidth - margin;
        const startY = margin;

        doc.addImage(base64Img, 'PNG', imageX, startY, imageWidth, imageHeight);

        // Encabezado del documento
        doc.setFontSize(24);
        doc.text("Restaurant UNA", margin, startY + 20);

        doc.setFontSize(16);
        doc.text(`Detalles del Pedido - Mesa ${mesa.mesaId}`, margin, startY + 60);

        doc.setFontSize(12);
        doc.text(`Fecha: ${new Date().toLocaleDateString()}`, margin, startY + 80);

        // Configurar la tabla
        const headers = [["Producto", "Cantidad", "Precio Unitario", "Total"]];
        const data = mesa.pedido.map((pedido) => [
            pedido.pedido,
            pedido.cant.toString(),
            `$${pedido.precio.toFixed(2)}`,
            `$${(pedido.cant * pedido.precio).toFixed(2)}`,
        ]);

        const total = mesa.pedido.reduce((acc, curr) => acc + curr.cant * curr.precio, 0);

        // Agregar la tabla al documento
        (doc as any).autoTable({
            startY: startY + 100,
            head: headers,
            body: data,
            theme: 'grid',
            styles: { fontSize: 12, cellPadding: 8 },
            headStyles: { fillColor: [41, 128, 185] }, // Azul oscuro
            bodyStyles: { fillColor: [255, 255, 255] }, // Blanco
            alternateRowStyles: { fillColor: [245, 245, 245] }, // Gris claro
            margin: { top: margin, bottom: margin, left: margin, right: margin },
        });

        // Agregar el total al documento
        const finalY = (doc as any).lastAutoTable.finalY + 20;
        doc.setFontSize(14);
        doc.text(`Total: $${total.toFixed(2)}`, margin, finalY);

        // Pie de página
        const footerY = doc.internal.pageSize.getHeight() - margin;
        doc.setFontSize(10);
        doc.text('¡Gracias por su visita!', margin, footerY - 10);
        doc.text('Restaurant UNA', pageWidth - margin - 60, footerY - 10);

        // Guardar el documento
        doc.save(`pedido_mesa_${mesa.mesaId}.pdf`);
    };


    // Función para obtener todas las mesas desde la API
    const fetchMesas = () => {
        fetch(`https://${import.meta.env.VITE_API_URL}.mockapi.io/api/Pedido`)
            .then(response => response.json())
            .then(data => setMesas(data))
            .catch(error => console.error('Error al obtener las mesas:', error));
    };

    // Función para agregar una nueva mesa
    const handleAddMesa = () => {
        fetch(`https://${import.meta.env.VITE_API_URL}.mockapi.io/api/Pedido`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newMesa),
        })
            .then(response => response.json())
            .then(data => {
                setMesas([...mesas, data]);
                setNewMesa({
                    mesaId: 0,
                    personas: 0,
                    status: true,
                    statusPedido: 1,
                    pedido: [],
                    createdAt: "",
                });
                setIsModalOpen(false);
                toast.success('Mesa agregada exitosamente.');
            })
            .catch(error => console.error('Error al agregar la mesa:', error));
    };

    // Función para actualizar una mesa
    const handleUpdateMesa = () => {
        if (!editingMesa || !editingMesa.id) return;

        fetch(`https://${import.meta.env.VITE_API_URL}.mockapi.io/api/Pedido/${editingMesa.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(editingMesa),
        })
            .then(response => response.json())
            .then(data => {
                setMesas(mesas.map(mesa => (mesa.id === data.id ? data : mesa)));
                setEditingMesa(null);
                setIsModalOpen(false);
                toast.success(`Mesa ${editingMesa.id} actualizada exitosamente.`);
            })
            .catch(error => console.error('Error al actualizar la mesa:', error));
    };

    // Función para eliminar una mesa
    const handleDeleteMesa = (id: string) => {
        fetch(`https://${import.meta.env.VITE_API_URL}.mockapi.io/api/Pedido/${id}`, {
            method: 'DELETE',
        })
            .then(response => {
                if (response.ok) {
                    setMesas(mesas.filter(mesa => mesa.id !== id));
                    toast.success(`Mesa ${id} eliminada exitosamente.`);
                    setIsDeleteModalOpen(false);
                } else {
                    throw new Error('No se pudo eliminar la mesa.');
                }
            })
            .catch(error => console.error('Error al eliminar la mesa:', error));
    };

    // Función para abrir el modal de pago
    const handleOpenPayModal = (mesa: Mesa) => {
        setSelectedMesa(mesa);
        setShowPayModal(true);
    };

    // Función para cerrar el modal de pago
    const handleClosePayModal = () => {
        setShowPayModal(false);
        setSelectedMesa(null);
    };

    const handlePayPedidoImprimir = () => {
        if (!selectedMesa) return;
        generatePDF(selectedMesa);
        handlePayPedido();
        toast.success(`El pedido de la mesa ${selectedMesa.id} ha sido pagado e impreso exitosamente.`);
    }

    // Función para realizar el pago del pedido
    const handlePayPedido = () => {

        if (!selectedMesa) return;

        // Actualizar el estado del pedido y la mesa a través de la API
        fetch(`https://${import.meta.env.VITE_API_URL}.mockapi.io/api/Pedido/${selectedMesa.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                statusPedido: 1, // Cambiar estado del pedido a pagado
                status: true, // Cambiar estado de la mesa a libre
                pedido: [],
                createdAt: "", // Limpiar los pedidos de la mesa
            }),
        })
            .then(response => {
                if (response.ok) {
                    // Actualizar el estado local de la mesa para reflejar los cambios
                    const updatedMesas = mesas.map(mesa => {
                        if (mesa.id === selectedMesa.id) {
                            return {
                                ...mesa,
                                statusPedido: 1,
                                status: true,
                                pedido: [],
                                createdAt: "", // Limpiar los pedidos de la mesa
                            };
                        }
                        return mesa;
                    });

                    setMesas(updatedMesas);
                    console.log(`Pedido de la mesa ${selectedMesa.id} pagado exitosamente.`);
                    toast.success(`El pedido de la mesa ${selectedMesa.id} ha sido pagado exitosamente.`);
                    handleClosePayModal(); // Cerrar el modal después de pagar el pedido
                } else {
                    throw new Error('No se pudo pagar el pedido.');
                }
            })
            .catch(error => {
                console.error('Error al pagar el pedido:', error);
            });
    };

    // Obtener las mesas al cargar el componente
    useEffect(() => {
        fetchMesas();
    }, []);

    const openModal = (mesa?: Mesa) => {
        if (mesa) {
            setEditingMesa(mesa);
        } else {
            setNewMesa({
                mesaId: 0,
                personas: 0,
                status: true,
                statusPedido: 1,
                pedido: [],
                createdAt: "",
            });
            setEditingMesa(null);
        }
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setEditingMesa(null);
    };

    return (
        <>
            <Navbar />
            <div className="p-8 pt-20 h-screen overflow-y-auto bg-slate-600">
                <h1 className="text-2xl font-bold mb-4 text-white animate-open-close">Gestión de Mesas</h1>

                <button
                    onClick={() => openModal()}
                    className="bg-blue-500 text-white py-2 px-4 rounded mb-4 animate-open-close"
                >
                    Agregar Mesa
                </button>

                <table className="w-full border-collapse text-center bg-white rounded-lg animate-open-close">
                    <thead>
                        <tr>
                            <th className="p-2">ID de Mesa</th>
                            <th className="border p-2">Max/Personas</th>
                            <th className="border p-2">Estado Mesa</th>
                            <th className="border p-2">Estado Pedido</th>
                            <th className="border p-2">Fecha de Pedido</th>
                            <th className="border p-2">Pedidos</th>
                            <th className="p-2">Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {mesas.map(mesa => (
                            <tr key={mesa.id}>
                                <td className="border-t p-2">{mesa.mesaId}</td>
                                <td className="border p-2">{mesa.personas}</td>
                                <td className={`border p-2 text-center ${mesa.status ? 'bg-green-400' : 'bg-red-400'}`}>
                                    <span className="font-bold text-white">
                                        {mesa.status ? 'Libre' : 'Ocupada'}
                                    </span>
                                </td>
                                <td className="border p-2 text-center">
                                    <span
                                        className={`font-bold ${mesa.statusPedido === 1
                                            ? 'text-green-600'
                                            : mesa.statusPedido === 2
                                                ? 'text-yellow-600'
                                                : 'text-purple-700'
                                            }`}
                                    >
                                        {mesa.statusPedido === 1 && 'Sin pedido'}
                                        {mesa.statusPedido === 2 && 'Preparando Pedido'}
                                        {mesa.statusPedido === 3 && 'DEBE PAGO'}
                                    </span>
                                </td>
                                <td className="border p-2">{mesa.createdAt}</td>
                                <td className="border p-2">
                                    <ul>
                                        {mesa.pedido.map((pedido, index) => (
                                            <li key={index}>{pedido.pedido} - Cantidad: {pedido.cant}</li>
                                        ))}
                                    </ul>
                                </td>
                                <td className="border-t p-2">
                                    <button
                                        onClick={() => openModal(mesa)}
                                        className="bg-yellow-500 text-white py-1 px-2 rounded m-2"
                                    >
                                        Editar
                                    </button>
                                    <button
                                        onClick={() => setIsDeleteModalOpen(true)}
                                        className="bg-red-500 text-white py-1 px-2 rounded m-2"
                                    >
                                        Eliminar
                                    </button>
                                    {
                                        isDeleteModalOpen && (
                                            <div className="fixed inset-0 flex items-center justify-center bg-opacity-50">
                                                <div className="bg-white p-6 rounded shadow-lg w-1/3">
                                                    <h2 className="text-xl font-bold mb-4">
                                                        ¿Está seguro de eliminar la mesa {mesa.mesaId}?
                                                    </h2>
                                                    <div className="flex justify-end">
                                                        <button
                                                            onClick={() => setIsDeleteModalOpen(false)}
                                                            className="bg-gray-500 text-white py-2 px-4 rounded mr-2"
                                                        >
                                                            Cancelar
                                                        </button>
                                                        <button
                                                            onClick={() => handleDeleteMesa(mesa.id!)}
                                                            className="bg-red-500 text-white py-2 px-4 rounded"
                                                        >
                                                            Eliminar
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        )
                                    }
                                    <button
                                        onClick={() => handleOpenPayModal(mesa)}
                                        className="bg-purple-700 text-white py-1 px-2 rounded m-2"
                                    >
                                        Pagar
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Modal de Agregar/Editar Mesa */}
            {isModalOpen && (
                <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
                    <div className="bg-white p-6 rounded shadow-lg w-1/3">
                        <h2 className="text-xl font-bold mb-4">
                            {editingMesa ? 'Editar Mesa' : 'Agregar Mesa'}
                        </h2>
                        <p className='font-bold'>N° de Mesa</p>
                        <input
                            type="number"
                            placeholder="ID de Mesa"
                            value={editingMesa ? editingMesa.mesaId : newMesa.mesaId}
                            onChange={e => {
                                const value = parseInt(e.target.value, 10);
                                if (editingMesa) {
                                    setEditingMesa({ ...editingMesa, mesaId: value });
                                } else {
                                    setNewMesa({ ...newMesa, mesaId: value });
                                }
                            }}
                            className="border p-2 mb-2 w-full"
                        />
                        <p className='font-bold'>Cantidad Maxima de Personas</p>
                        <input
                            type="number"
                            placeholder="Personas"
                            value={editingMesa ? editingMesa.personas : newMesa.personas}
                            onChange={e => {
                                const value = parseInt(e.target.value, 10);
                                if (editingMesa) {
                                    setEditingMesa({ ...editingMesa, personas: value });
                                } else {
                                    setNewMesa({ ...newMesa, personas: value });
                                }
                            }}
                            className="border p-2 mb-2 w-full"
                        />
                        <div className="flex justify-end">
                            <button
                                onClick={closeModal}
                                className="bg-gray-500 text-white py-2 px-4 rounded mr-2"
                            >
                                Cancelar
                            </button>
                            <button
                                onClick={() => setisConfirm(true)}
                                className="bg-blue-500 text-white py-2 px-4 rounded"
                            >
                                {editingMesa ? 'Actualizar' : 'Agregar'}
                            </button>
                            {
                                isConfirm && (
                                    <div className="fixed inset-0 flex items-center justify-center bg-opacity-50">
                                        <div className="bg-white p-6 rounded shadow-lg w-1/3">
                                            <h2 className="text-xl font-bold mb-4">
                                                ¿Está seguro de {editingMesa ? 'actualizar' : 'agregar'} la mesa {editingMesa?.mesaId}?
                                            </h2>
                                            <div className="flex justify-end">
                                                <button
                                                    onClick={() => setisConfirm(false)}
                                                    className="bg-gray-500 text-white py-2 px-4 rounded mr-2"
                                                >
                                                    Cancelar
                                                </button>
                                                <button
                                                    onClick={editingMesa ? handleUpdateMesa : handleAddMesa}
                                                    className="bg-blue-500 text-white py-2 px-4 rounded"
                                                >
                                                    {editingMesa ? 'Actualizar' : 'Agregar'}
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                )
                            }
                        </div>
                    </div>
                </div>
            )}

            {/* Modal de Pago */}
            {showPayModal && (
                <div className="fixed inset-0 flex items-start justify-center bg-black bg-opacity-50 overflow-auto  z-10 pt-20">
                    <div className="bg-white p-6 rounded shadow-lg w-1/3">
                        <h2 className="text-xl font-bold mb-4">
                            Pagar Pedido de Mesa {selectedMesa?.mesaId}
                        </h2>
                        <table className="w-full mb-4">
                            <thead>
                                <tr className="bg-gray-200">
                                    <th className="p-2">Pedido</th>
                                    <th className="p-2">Cantidad</th>
                                    <th className="p-2">Precio x1</th>
                                    <th className="p-2">Total</th>
                                </tr>
                            </thead>
                            <tbody>
                                {selectedMesa?.pedido.map((pedido, index) => (
                                    <tr key={index} className="border-b">
                                        <td className="p-2">{pedido.pedido}</td>
                                        <td className="p-2">x{pedido.cant}</td>
                                        <td className="p-2">₡{pedido.precio}</td>
                                        <td className="p-2">₡{pedido.cant * pedido.precio}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                        <p className="font-bold text-red-600 text-2xl">
                            Total: ₡{selectedMesa?.pedido.reduce((acc, curr) => acc + curr.cant * curr.precio, 0)}
                        </p>
                        <div className="flex justify-end mt-4">
                            <button
                                onClick={handleClosePayModal}
                                className="bg-gray-500 text-white py-2 px-4 rounded mr-2"
                            >
                                Cancelar
                            </button>
                            <button
                                onClick={handlePayPedido}
                                className="bg-purple-800 text-white py-2 px-4 rounded mr-2"
                            >
                                Pagar Pedidos
                            </button>
                            <button
                                onClick={handlePayPedidoImprimir}
                                className="bg-cyan-600 text-white py-2 px-4 rounded"
                            >
                                Pagar e imprimir
                            </button>
                        </div>
                    </div>
                </div>
            )}


            <Footer />
        </>
    );
};

export default TablesPage;
